mapper.select:
select mno,title, message, spand_name,spand_date,read
from(select e.mno,e.title, m.member_Id, e.message, e.spand_name,e.spand_date,e.read,ceil(rownum/10) as page 
from message e,member m where e.member_Id=m.member_Id and m.member_Id=#{member_id} order by e.spand_date desc) 
where page=#{pageNo}

select e.title, m.member_id, e.message, e.spand_name,e.spand_date,e.mno
from message e,member m
where e.member_id=m.member_id and m.member_id=#{member_id} and e.mno=#{mno}
